//m-3-id-1 提示信息的左右滚动
(function() {
    var marquee = document.getElementById('message');
    var offset = 0;
    var scrollwidth = marquee.offsetWidth;

    setInterval(function() {
        if (offset == 2 * scrollwidth) {
            offset = 0;
        }
        marquee.style.marginLeft = scrollwidth - offset + "px";
        offset += 2;
    }, 15);
})();

